        h2, h3 {
            color: black;
        }

        summary {
            cursor: pointer;
            font-weight: bold;
            
        }

        section {
            margin-bottom: 70px;
            padding-bottom: 10px;
            border-bottom: 1px solid #eee;
        }

        p {
            margin: 0;
            padding: 5px 0;
            line-height: 1.5;
            color: #333;
        }

        ul {
            list-style: none;
            padding: 0;
            margin: 0;
        }

        li {
            margin-bottom: 10px;
        }

        footer {
            text-align: center;
            background-color: #333;
            color: #fff;
            position: fixed;
            bottom: 0;
            width: 100%;
        }
    </style>
   
